import { Component } from '@angular/core';
import { Student } from 'src/app/_models/student';

@Component({
  selector: 'app-student-details',
  templateUrl: './student-details.component.html',
  styleUrls: ['./student-details.component.css'],
})
export class StudentDetailsComponent {
  std: Student = new Student();
  changeId(id: any) {
    this.std.Id = parseInt(id.value);
  }
}
<div>
  Student Data
  <div>
    <label for="std.id"></label>
    <input type="number" [value]="std.Id" (keyup)="changeId($event.target)" />
    <div>Id:{{ std.Id }}</div>
  </div>
  <div>
    <label for="std.name"></label>
    <input type="text" #t1 [value]="std.Name" ((keyup))="std.Name = t1.value" />
    <div>Id:{{ std.Name }}</div>
    Name:{{ std.Name }}
  </div>
  <div>Age:{{ std.Age }}</div>
</div>
