import { Component, EventEmitter, Input, Output } from '@angular/core';
import { Department } from 'src/app/models/department';

@Component({
  selector: 'app-edit-department',
  templateUrl: './edit-department.component.html',
  styleUrls: ['./edit-department.component.css'],
})
export class EditDepartmentComponent {
  @Input() deptt = new Department();
  dept: Department = new Department();
  deptToEdit = new Department();
  @Output() editDept: EventEmitter<Department> = new EventEmitter();
  edit() {
    this.editDept.emit(this.deptToEdit);
  }
}
<form>
  <fieldset>
    <label for="Id">ID:</label>
    <input type="text" name="Id" [(ngModel)]="dept.id" />
  </fieldset>
  <fieldset>
    <label for="Name">Name:</label>
    <input type="text" name="Name" [(ngModel)]="dept.Name" />
  </fieldset>
  <fieldset>
    <label for="Location">Location:</label>
    <input type="text" name="Location" [(ngModel)]="dept.Location" />
  </fieldset>
  <button>Edit</button>
</form>
