import { Component, EventEmitter, Output } from '@angular/core';
import { DepartmentService } from 'src/app/Services/department.service';
import { Department } from 'src/app/models/department';

@Component({
  selector: 'app-department-list',
  templateUrl: './department-list.component.html',
  styleUrls: ['./department-list.component.css'],
})
export class DepartmentListComponent {
  deptSrv: DepartmentService = new DepartmentService();
  departments: Department[] = [];
  deptDetails: Department = new Department();
  deptToEdit: Department = new Department();
  detailsFlag: boolean = false;
  ngOnInit(): void {
    this.departments = this.deptSrv.getDepartments();
  }
  showDetails(id: number) {
    // this.departments.forEach((dept) => {
    //   if (dept.id == id) {
    //     this.deptDetails = dept;
    //     this.detailsFlag = true;
    //   }
    // });
  }
  hideDetails() {
    this.detailsFlag = false;
  }
  addDept(dept: Department) {
    this.departments.push(dept);
  }
  removeDept(id: number) {
    for (let i = 0; i < this.departments.length; i++) {
      if (this.departments[i].id == id) this.departments.splice(i, 1);
    }
  }
  editDept(dept: Department) {
    this.departments.forEach((d, i) => {
      if (d.id == dept.id) {
        this.departments[i].Name = dept.Name;
        this.departments[i].Location = dept.Location;
      }
    });
  }
  sendEditDepartment(id: number) {
    this.departments.forEach((d, i) => {
      if (d.id == id) {
        this.deptToEdit.id = this.departments[i].id;
        this.deptToEdit.Name = this.departments[i].Name;
        this.deptToEdit.Location = this.departments[i].Location;
      }
    });
  }
}
<app-add-department (newDept)="addDept($event)"></app-add-department>
<div class="table-responsive">
  <table class="table table-primary">
    <thead>
      <tr>
        <th scope="col">Id</th>
        <th scope="col">Name</th>
        <th scope="col">Location</th>
        <th scope="col">Actions</th>
      </tr>
    </thead>
    <tbody>
      <tr class="" *ngFor="let item of departments">
        <td scope="row">{{ item.id }}</td>
        <td>{{ item.Name }}</td>
        <td>{{ item.Location }}</td>
        <td>
          <button (click)="showDetails(item.id)">Details</button>
          <button (click)="sendEditDepartment(item.id)">Edit</button>
          <button (click)="removeDept(item.id)">Delete</button>
        </td>
      </tr>
    </tbody>
  </table>
  <app-department-details
    [deptDetails]="deptDetails"
    *ngIf="detailsFlag"
    (hideOutput)="hideDetails()"
  ></app-department-details>
</div>
<app-edit-department
  [dept]="deptToEdit"
  (editDept)="editDept($event)"
></app-edit-department>
