import { Component, Output, EventEmitter } from '@angular/core';
import { DepartmentService } from 'src/app/Services/department.service';
import { Department } from 'src/app/models/department';
import { Router } from '@angular/router';
@Component({
  selector: 'app-add-department',
  templateUrl: './add-department.component.html',
  styleUrls: ['./add-department.component.css'],
})
export class AddDepartmentComponent {
  dept: Department = new Department();
  constructor(public deptSrv: DepartmentService, private router: Router) {}
  // @Output() newDept: EventEmitter<Department> = new EventEmitter<Department>();
  addDepartment() {
    // this.newDept.emit(this.dept);
    this.deptSrv.addDepartment(this.dept).subscribe((data) => {
      this.router.navigateByUrl('/departments');
    });
  }
}
<form>
  <fieldset>
    <label for="Id">ID:</label>
    <input type="text" name="Id" [(ngModel)]="dept.id" />
  </fieldset>
  <fieldset>
    <label for="Name">Name:</label>
    <input type="text" name="Name" [(ngModel)]="dept.name" />
  </fieldset>
  <fieldset>
    <label for="Location">Location:</label>
    <input type="text" name="Location" [(ngModel)]="dept.location" />
  </fieldset>
  <button (click)="addDepartment()">Add</button>
</form>
